// 🎯 KEYWORD PRIORITY CONFIGURATION
// Configures batch processing and keyword prioritization

const priorityConfig = {
  // 📊 BATCH SIZES (optimized for performance)
  performance_batch_size: 100,
  keywords_batch_size: 50,
  indexing_batch_size: 100,
  tracked_keywords_limit: 200,

  // ⚡ PERFORMANCE SETTINGS
  batch_delay: 2000,
  query_timeout: 45000,
  max_days_back: 30,

  // 🎯 KEYWORD PRIORITY THRESHOLDS
  priority_scoring: {
    high_impressions_threshold: 10000,    // High volume keywords
    medium_impressions_threshold: 1000,   // Medium volume keywords
    low_ctr_threshold: 2.0,               // CTR below 2%
    good_ctr_threshold: 5.0,              // CTR above 5%
    high_position_threshold: 3,           // Top 3 positions
    medium_position_threshold: 10,        // Top 10 positions
    low_position_threshold: 20,           // Top 20 positions
    min_clicks_threshold: 5               // Minimum clicks for relevance
  },

  // 🔢 PRIORITY SCORING WEIGHTS
  scoring_weights: {
    impressions_weight: 0.3,              // 30% - Search volume importance
    position_weight: 0.25,                // 25% - Current ranking importance
    ctr_weight: 0.2,                      // 20% - Click-through rate importance
    clicks_weight: 0.15,                  // 15% - Actual traffic importance
    trend_weight: 0.1                     // 10% - Performance trend importance
  }
};

// 📅 DATE RANGE CALCULATION
const dateRange = {
  start_date: new Date(Date.now() - (priorityConfig.max_days_back * 24 * 60 * 60 * 1000)).toISOString().split('T')[0],
  end_date: new Date(Date.now() - (24 * 60 * 60 * 1000)).toISOString().split('T')[0],
  analysis_date: new Date().toISOString().split('T')[0]
};

// 🎯 EXECUTION METADATA
const executionMetadata = {
  priority_config: priorityConfig,
  date_range: dateRange,
  execution_id: `seo_priority_${Date.now()}`,
  start_time: new Date().toISOString(),
  workflow_version: '2.2-priority'
};

console.log('🎯 Keyword Priority Configuration Loaded');
console.log(`📅 Date Range: ${dateRange.start_date} to ${dateRange.end_date}`);
console.log(`🔢 Batch Size: ${priorityConfig.keywords_batch_size} keywords`);

return { json: executionMetadata };

// 🎯 KEYWORD LIST PROCESSOR
// Processes and validates tracked keywords from Google Sheets

try {
  // 📊 Extract keywords data
  const keywordsData = $input.all() || [];

  // 🛡️ Validate and clean keyword data
  const validKeywords = keywordsData
    .filter(item => item.json && item.json.Keyword && item.json.Keyword.trim())
    .map(item => ({
      keyword: item.json.Keyword.trim().toLowerCase(),
      priority: item.json.Priority || 'Medium',
      target_position: parseInt(item.json['Target Position']) || 10,
      business_value: parseInt(item.json['Business Value']) || 5,
      notes: item.json.Notes || '',
      manual_priority: true
    }));

  // 🎯 Create keyword filter for BigQuery
  const keywordsList = validKeywords.map(k => `'${k.keyword.replace(/'/g, "''")}'`).join(', ');

  // 📊 Priority distribution
  const priorityCount = {
    high: validKeywords.filter(k => k.priority.toLowerCase() === 'high').length,
    medium: validKeywords.filter(k => k.priority.toLowerCase() === 'medium').length,
    low: validKeywords.filter(k => k.priority.toLowerCase() === 'low').length
  };

  // 🎯 Processed keyword data
  const processedKeywords = {
    tracked_keywords: validKeywords,
    keywords_list: keywordsList,
    total_keywords: validKeywords.length,
    priority_distribution: priorityCount,
    keywords_query_filter: validKeywords.length > 0 ? 
      `AND LOWER(query) IN (${keywordsList})` : '',
    processing_status: {
      success: true,
      keywords_loaded: validKeywords.length,
      has_tracked_keywords: validKeywords.length > 0
    }
  };

  console.log(`✅ Tracked Keywords Loaded: ${validKeywords.length}`);
  console.log(`🎯 Priority Distribution - High: ${priorityCount.high}, Medium: ${priorityCount.medium}, Low: ${priorityCount.low}`);

  return { json: processedKeywords };

} catch (error) {
  console.error('❌ Keyword Processing Error:', error.message);

  return {
    json: {
      tracked_keywords: [],
      keywords_list: '',
      total_keywords: 0,
      priority_distribution: { high: 0, medium: 0, low: 0 },
      keywords_query_filter: '',
      processing_status: {
        success: false,
        keywords_loaded: 0,
        has_tracked_keywords: false,
        error: error.message
      }
    }
  };
}

// 🎯 COMPREHENSIVE DATA PROCESSOR
// Combines keyword priority analysis with performance data

try {
  // 📊 Extract all data sources
  const keywordData = $input.all().find(item => item.json.query)?.json || {};
  const performanceData = $input.all().find(item => item.json.page)?.json || {};
  const trackedKeywords = $("Process Keywords").first()?.json?.tracked_keywords || [];
  const priorityConfig = $json.priority_config || {};

  // 🛡️ Data validation
  const validKeywordData = Array.isArray(keywordData) ? keywordData : (keywordData.query ? [keywordData] : []);
  const validPerformanceData = Array.isArray(performanceData) ? performanceData : (performanceData.page ? [performanceData] : []);

  // 🎯 Process keyword priorities
  const keywordPriorities = validKeywordData.map(keyword => {
    // Find matching tracked keyword
    const trackedKeyword = trackedKeywords.find(tk => 
      tk.keyword === keyword.query?.toLowerCase()
    );

    return {
      ...keyword,
      is_tracked: !!trackedKeyword,
      manual_priority: trackedKeyword?.priority || null,
      business_value: trackedKeyword?.business_value || null,
      target_position: trackedKeyword?.target_position || null,
      notes: trackedKeyword?.notes || null
    };
  });

  // 📊 Comprehensive analysis data
  const comprehensiveData = {
    // 🎯 KEYWORD ANALYSIS
    keyword_priorities: keywordPriorities,
    keyword_summary: {
      total_analyzed: keywordPriorities.length,
      tracked_keywords: keywordPriorities.filter(k => k.is_tracked).length,
      critical_priority: keywordPriorities.filter(k => k.final_priority === 'CRITICAL').length,
      high_priority: keywordPriorities.filter(k => k.final_priority === 'HIGH').length,
      medium_priority: keywordPriorities.filter(k => k.final_priority === 'MEDIUM').length,
      low_priority: keywordPriorities.filter(k => k.final_priority === 'LOW').length
    },

    // 📊 PERFORMANCE ANALYSIS
    performance_data: validPerformanceData,
    performance_summary: {
      total_pages: validPerformanceData.length,
      tracked_pages: validPerformanceData.filter(p => (p.tracked_keyword_count || 0) > 0).length,
      high_priority_pages: validPerformanceData.filter(p => p.page_priority === 'HIGH').length,
      total_clicks: validPerformanceData.reduce((sum, p) => sum + (parseInt(p.total_clicks) || 0), 0),
      total_impressions: validPerformanceData.reduce((sum, p) => sum + (parseInt(p.total_impressions) || 0), 0)
    },

    // 🎯 PRIORITY OPPORTUNITIES
    opportunities: {
      position_opportunities: keywordPriorities.filter(k => k.opportunity_type === 'POSITION_OPPORTUNITY'),
      ctr_opportunities: keywordPriorities.filter(k => k.opportunity_type === 'CTR_OPPORTUNITY'),
      visibility_opportunities: keywordPriorities.filter(k => k.opportunity_type === 'VISIBILITY_OPPORTUNITY'),
      declining_keywords: keywordPriorities.filter(k => k.opportunity_type === 'DECLINING_KEYWORD'),
      high_value_monitors: keywordPriorities.filter(k => k.opportunity_type === 'HIGH_VALUE_MONITOR')
    },

    // 📅 METADATA
    analysis_date: new Date().toISOString().split('T')[0],
    processing_time: new Date().toISOString(),

    // 🔧 PROCESSING STATUS
    processing_status: {
      success: true,
      keyword_analysis_complete: true,
      performance_analysis_complete: true,
      total_records: keywordPriorities.length + validPerformanceData.length,
      has_tracked_keywords: trackedKeywords.length > 0
    }
  };

  console.log(`✅ Comprehensive Analysis Complete:`);
  console.log(`🎯 Keywords Analyzed: ${keywordPriorities.length}`);
  console.log(`📊 Pages Analyzed: ${validPerformanceData.length}`);
  console.log(`🏆 Critical Keywords: ${comprehensiveData.keyword_summary.critical_priority}`);
  console.log(`📈 High Priority Keywords: ${comprehensiveData.keyword_summary.high_priority}`);

  return { json: comprehensiveData };

} catch (error) {
  console.error('❌ Comprehensive Analysis Error:', error.message);

  return {
    json: {
      keyword_priorities: [],
      keyword_summary: { total_analyzed: 0, tracked_keywords: 0, critical_priority: 0, high_priority: 0, medium_priority: 0, low_priority: 0 },
      performance_data: [],
      performance_summary: { total_pages: 0, tracked_pages: 0, high_priority_pages: 0, total_clicks: 0, total_impressions: 0 },
      opportunities: { position_opportunities: [], ctr_opportunities: [], visibility_opportunities: [], declining_keywords: [], high_value_monitors: [] },
      analysis_date: new Date().toISOString().split('T')[0],
      processing_time: new Date().toISOString(),
      processing_status: {
        success: false,
        keyword_analysis_complete: false,
        performance_analysis_complete: false,
        total_records: 0,
        has_tracked_keywords: false,
        error: error.message
      }
    }
  };
}

