const gscData = $input.first().json;
const indexingData = $input.last().json;

const combineData = {
  performance_data: gscData,
  indexing_data: indexingData,
  analysis_date: new Date().toISOString().split('T')[0],
  total_pages_analyzed: gscData.length,
  indexing_issues: indexingData.length
};

return { json: combineData };

const response = $json.content[0].text;

const formatForTelegram = {
  report: response,
  timestamp: new Date().toISOString(),
  chat_id: $env.TELEGRAM_CHAT_ID,
  parse_mode: 'Markdown'
};

return { json: formatForTelegram };

const success = $json.ok;
const messageId = $json.message_id;

console.log('Telegram Status:', success ? 'SUCCESS' : 'FAILED');
console.log('Message ID:', messageId);

return { 
  json: {
    success: success,
    message_id: messageId,
    timestamp: new Date().toISOString()
  }
};
